jQuery("#content_box a[href^=http]:not([href*='science.nasa.gov']):not(:has(img:only-child))").addClass("external");
jQuery("#news_banner a[href^=http]:not([href*='science.nasa.gov'])").addClass("external");
jQuery('<span class="close">Close</span>').click(function () {
    $("#alert").slideUp("slow")
}).prependTo("#alert");
jQuery("input.clear_on_focus").each(function (b, c) {
    var a = jQuery(c);
    if (!c.value && c.title) {
        a.val(c.title).addClass("no_value")
    } else {
        a.removeClass("no_value")
    }
    jQuery(c.form).submit(function () {
        c.value = c.value.replace(c.title, "")
    });
    jQuery(c).focus(function () {
        c.value = c.value.replace(c.title, "");
        a.removeClass("no_value")
    });
    jQuery(c).blur(function () {
        if (!c.value && c.title) {
            a.val(c.title).addClass("no_value")
        } else {
            a.removeClass("no_value")
        }
    })
});
